mapper.select:
select *
from chat
where (send_user_id = #{send_user_id}
and receive_user_id = #{receive_user_id})
or (send_user_id = #{receive_user_id}
and receive_user_id = #{send_user_id})

select count(*)
from chat
where (send_user_id = #{send_user_id}
and receive_user_id = #{receive_user_id})
or (send_user_id = #{receive_user_id}
and receive_user_id = #{send_user_id})
